import React, { useState } from 'react';
import Modal from 'react-modal';

Modal.setAppElement("#root");

export default function Thumbnail(props) {
  const [isOpen, setIsOpen] = useState(false);

  function toggleModal() {
    setIsOpen(!isOpen)
  }

  return(
    <div onClick={ toggleModal } className={ props.className }>
      <img className="thumbnail-img" alt="sort-viz" src={ props.gifPath }></img>
      <p> {props.name} </p>
      <Modal
        isOpen = { isOpen }
        onRequestClose = { toggleModal }
        contentLabel = "My dialog"
        className = "modal"
        overlayClassName = "overlay"
        closeTimeoutMS = { 400 }
      >
        <div className="modal-title">{props.name}</div>
        <div className="modal-description">{props.description}</div>
      </Modal>
    </div>
  )
}
