import React, {useState} from 'react';

function sleep (time) {
  return new Promise((resolve) => setTimeout(resolve, time));
}

class TitlePage extends React.Component {
  constructor(props) {
    super(props);


    this.state = {
      text: ''
    }
  }

  componentDidMount() {
    this.handleType();
  }

  handleType = () => {
    const justinsantosText = 'justinsantos';
    const midNameTypingDelay = 2250;
    const onLoadTypingDelay = 7000;
    const typingDelay = 125;
    const { text } = this.state;

    if (text.length === 0) {
      sleep(onLoadTypingDelay)
    }

    this.setState({
      text: justinsantosText.substring(0, text.length + 1)
    });

    setTimeout(this.handleType, typingDelay);
  };

  render() {
    return (
      <h1>>&nbsp;
        <span>{ this.state.text }</span>
        <span id="cursor"/>
      </h1>
    );

  }
}

export default TitlePage;
