import React, { useState } from 'react';
import Modal from 'react-modal';

Modal.setAppElement("#root");

export default function Thumbnail(props) {
  const [isOpen, setIsOpen] = useState(false);

  function toggleModalOpen() {
    setIsOpen(true)
  }

  function toggleModalClose() {
    setIsOpen(false)
  }

  return(
    <div onClick={ toggleModalOpen } className={ props.className }>
      <img className="thumbnail-img" alt="alt" src={ props.gifPath }></img>
      <p> {props.name} </p>
      <Modal
        isOpen = { isOpen }
        onRequestClose = { toggleModal }
        contentLabel = "My dialog"
        className = "modal"
        overlayClassName = "overlay"
        closeTimeoutMS = { 400 }
      >
        <div className="modal-header">
          <div className="modal-title">{props.name}</div>
          <i class="fas fa-times"></i>
        </div>
        <div className="modal-description">{props.description}</div>
      </Modal>
    </div>
  )
}
