import React from 'react';
import Modal from '../modal/modal';

class Thumbnail extends React.Component {
  state = {
    showModal: false
  }

  showModal = e => {
    console.log(this.state.showModal)
    this.setState({
      showModal: true
    })
  }

  render() {
    return(
      <div onClick={e => { this.showModal(); }} className={ this.props.className }>
        <img className="thumbnail-img" src={ this.props.gifPath }></img>
        <p> {this.props.name} </p>
        <Modal showModal={this.state.showModal}> Modal message </Modal>
      </div>
    )
  }
}

export default Thumbnail
