import React, {useState} from 'react';

class TitlePage extends React.Component {
  constructor(props) {
    super(props);
    const justinsantosText = "justinsantos";
    const midNameTypingDelay = 2250;
    const onLoadTypingDelay = 1500;
    const typingDelay = 125;

    this.state = {
      text: ''
    }
  }

  componentDidMount() {
    setTimeout(this.handleType, this.onLoadTypingDelay);
    this.handleType();
  }

  handleType = () => {
    const { text } = this.state;

    this.setState({
      text: this.justinsantosText.substring(0, text.length + 1)
    });

    setTimeout(this.handleType, typingDelay);
  };

  render() {
    return (
      <h1>>&nbsp;
        <span>{ this.state.text }</span>
        <span id="cursor"/>
      </h1>
    );

  }
}

export default TitlePage;
